mapper.update:
update t_permission
set name = #{name,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR},
  seqno = #{seqno,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, url, icon, pid, seqno
from t_permission
where id = #{id,jdbcType=INTEGER}
